<?php
    if (!isset($_COOKIE['theme'])) {
        setcookie('theme', 'dark', time() + (20 * 365 * 24 * 60 * 60));
    }

    function toggleTheme(string $theme = "toggle"): void {
        switch ($theme) {
            case "toggle":
                if ($_COOKIE['theme'] == "dark") {
                   setcookie('theme', 'light', time() + (20 * 365 * 24 * 60 * 60));
                } elseif ($_COOKIE['theme'] == "light") {
                   setcookie('theme', 'dark', time() + (20 * 365 * 24 * 60 * 60));
                }
                break;
            case "dark":
               setcookie('theme', 'dark', time() + (20 * 365 * 24 * 60 * 60));
                break;
            case "lignt":
               setcookie('theme', 'light', time() + (20 * 365 * 24 * 60 * 60));
                break;
            default:
        }
    }

    if (isset($_GET) && array_key_exists("toggleTheme", $_GET)) {
        if (!empty($_GET["toggleTheme"])) {
            toggleTheme($_GET['toggleTheme']);
        } else {
            toggleTheme();
        }
        header("Location: ./");
    }
?>

